from functools import lru_cache


class Solution:
    def appealSum(self, s: str) -> int:
        @lru_cache(None)
        def check_c(c):
            ans=0
            for v in c:
                if v>='1':
                    ans+=1
            return ans
        ans=0
        def check(n): # 长度为n的
            # n=3
            ans=0
            c=[0]*26
            for i in range(n):
                c[ord(s[i])-ord('a')]+=1
            # print(s[0:n],end=" ")
            ans+=check_c("".join([str(i) for i in c]))
            for i in range(n,len(s)):
                c[ord(s[i-n])-ord('a')]-=1
                c[ord(s[i])-ord('a')]+=1
                ans+=check_c("".join([str(i) for i in c]))
            return ans
        for i in range(1,len(s)+1):
            ans+=check(i)
        return ans
a=""
print(Solution().appealSum(a))